mapper.select:
select count(id)
from user_portrait_by_visited_table_everyday


    user_name = #{userName}


    and visited_table like concat('%',#{visitedTable},'%')


    and visited_date  >=  #{visitedDate}


    and visited_date  <=  #{visitedDate}

select
id, user_name, rule_name, visited_table, visited_count, visited_date
from user_portrait_by_visited_table_everyday


    user_name = #{userName}


    and visited_table like concat('%',#{visitedTable},'%')


    and visited_date  >=  #{visitedDate}


    and visited_date  <=  #{visitedDate}


order by visited_date desc

  LIMIT #{pageNo},#{pageSize}

update.foreach:
update user_portrait_by_visited_table_everyday


    visited_count = #{item.visitedCount},


where user_name=#{item.userName} and visited_table=#{item.visitedTable} and visited_date=#{item.visitedDate}
